module.exports.create_new_game_session = function(id, user_id1){
    var game_seesion = {};
    game_seesion.id = id;
    game_seesion.users_id = [
        user_id1
    ];
    game_seesion.state = [
        [0, 0, 0],
        [0, 0, 0],
        [0, 0, 0]
    ];
    game_seesion.current_user = 0;
    game_seesion.finished = false;
    game_seesion.started = false;

    game_seesion.add_user = function(userid){
        this.users_id.push(userid);
        this.started = true;
    };

    return game_seesion;
};